sysauto_script.script:
var api = gs.getProperty('x_clost_google_cal.api_key');
var country = gs.getProperty('x_clost_google_cal.cal_country_code');

var r = new sn_ws.RESTMessageV2('x_clost_google_cal.Calendar', 'Default GET');
r.setStringParameterNoEscape('yourAPIKey', api);
r.setStringParameterNoEscape('holiday_cal', country);

var response = r.execute();
var responseBody = response.getBody();
var httpStatus = response.getStatusCode();

var parser = new global.JSON();
var parsed = parser.decode(responseBody);

createCal(parsed.items, parsed.summary);

function createCal(items, summary) {
	gs.info('AH: In Create CAL function: summary is ' + summary);
    var createCalName = new GlideRecord('cmn_schedule');
    createCalName.addQuery('name', summary);
    createCalName.query();

    if (createCalName.next()) {
        updateCalEntry(items, createCalName.sys_id);
    } else if (!createCalName.next()) {
        createCalName.initialize();
        createCalName.name = parsed.summary;
		createCalName.is_legacy_schedule = true;
        var calSys = createCalName.insert();
		gs.info('CAL Name: '  + parsed.summary + ' - ' + createCalName.name + ' - ' + calSys);
        updateCalEntry(items, calSys);
    }
}

function updateCalEntry(calEntrys, calSys) {
		gs.info('AH: In Update CAL function: calSys is ' + calSys);

    for (i = 0; i < calEntrys.length; i++) {
        gs.info('CAL Entry' + [i] + ' : ' + calEntrys[i].summary + ' - ' + calEntrys[i].start.date);
        var createCalEntry = new GlideRecord('cmn_schedule_span');
        createCalEntry.addQuery('schedule', calSys);
        createCalEntry.addQuery('name', calEntrys[i].summary);
        createCalEntry.addQuery('start_date_time', calEntrys[i].start.date);
        createCalEntry.query();

        if (createCalEntry.next()) {
            //Do Nothing
        } else if (!createCalEntry.next()) {
            // create new cal record
            gs.info('CAL Missing');
        }
    }
}
